import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import SideBar from "./sidebar/SideBar.jsx"
import NavBar from "./content/Navbar.jsx"

const AllProject = () => {
  const navigate = useNavigate();
  const [data, setdata] = useState([]);
  useEffect(() => {
    axios
      .get("http://localhost:4000/api/projects/")
      .then((result) => {
        setdata(result.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);
  const [owner, setowner] = useState("");

  const theOwner = (e) => {
    axios
      .get(`http://localhost:4000/api/users/${e.owner}`)
      .then((result) => {
        setowner(result.data[0].userName);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div>

    <div className="card-container">
      {data &&
        data.map((e, i) => {
          
          return (
            <div
            class="card-client"
            
            >
              <div class="user-picture">
                <img
                  src={e.sreenshotLink}
                  alt={e.name}
                  onClick={() => {
                    navigate("/allproject/oneproject", {
                      state: { element: e, owner: owner },
                    });
                  }}
                  />
              </div>
              <p class="name-client"> {e.name}</p>
              <p class="name-client"> {}</p>
              <a href={e.viewlink}> Open the website</a>
            </div>
          );
        })}
    </div>
</div>
  );
};

export default AllProject;
