import React, { useState } from "react";
import "../App.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Login = (props) => {
  const navigate = useNavigate();
  const [message, setmessage] = useState("");

  const checkUser = async (email, password) => {
    try {
      const emailResponse = await axios.get(
        `http://localhost:4000/api/users/getbyemail/${email}`
      );
      if (emailResponse.data.length === 0) {
        setmessage("Email not registered");
        return;
      }

      const passwordResponse = await axios.get(
        `http://localhost:4000/api/users/getbyemailandpassword/${email}/${password}`
      );

      if (passwordResponse.data.length === 0) {
        setmessage("Invalid password");
      } else {
        props.userIn(passwordResponse.data[0]);
        navigate("/allprojects");
      }
    } catch (error) {
      console.log(error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const email = document.getElementById("email").value;
    const password = document.getElementById("pass").value;

    if (email && password) {
      checkUser(email, password);
    } else {
      setmessage("Please fill in both email and password fields");
    }
  };

  return (
    <form className="form">
      <p className="title">Login</p>
      <p className="message">Login now and get full access to our app.</p>

      <label>
        <input
          id="email"
          required
          placeholder=""
          type="email"
          className="input"
        />
        <span>Email</span>
      </label>
      <label>
        <input
          id="pass"
          required
          placeholder=""
          type="password"
          className="input"
        />
        <span>Password</span>
      </label>
      <p>{message}</p>
      <button className="submit" onClick={handleSubmit}>
        Login
      </button>
      <p className="signin">
        Haven't an account?
        <a
          onClick={() => {
            navigate("/signup");
          }}
        >
          SIGN UP
        </a>
      </p>
    </form>
  );
};

export default Login;
