import React, { useState } from "react";
import "../App.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";
const Login = (props) => {
  const navigate = useNavigate();
  const [message, setmessage] = useState("");
  // const emailsaved = (email) => {
  //   axios
  //     .get(`http://localhost:4000/api/users/getbyemail/${email}`)
  //     .then((result) => {
  //       console.log("ðŸš€ ~ file: Login.jsx:12 ~ .then ~ result:", result)
  //       props.userIn(result.data);
  //       return true;
  //     })
  //     .catch((error) => {
  //       return false;
  //     });
  // };
  const confirmuser = (email, password) => {
    axios
      .get(
        `http://localhost:4000/api/users/getbyemailandpassword/${email}/${password}`
      )
      .then((result) => {
      console.log("ðŸš€ ~ file: Login.jsx:26 ~ .then ~ result:", result.data.length)
        if (result.data.length===0) {
          return false
        }else{
          props.userIn(result.data[0]);
          return true
        }
      })
      .catch((error) => {
        console.log(error);
      });
      
  };
  const verifuser = (obj) => {
    if (confirmuser(obj.email, obj.password)===false) {

      setmessage("password invalid ");
    } else {
      console.log("hello "+obj.userName);
      navigate("/allprojects");
    }
  };

  return (
    <form class="form">
      <p class="title">Login </p>
      <p class="message">Login now and get full access to our app. </p>

      <label>
        <input
          id="email"
          required=""
          placeholder=""
          type="email"
          class="input"
        />
        <span>Email</span>
      </label>
      <label>
        <input
          id="pass"
          required=""
          placeholder=""
          type="password"
          class="input"
        />
        <span>Password</span>
      </label>
      <p>{message}</p>
      <button
        class="submit"
        onClick={(e) => {
          e.preventDefault();
          verifuser({
            email: document.getElementById("email").value,
            password: document.getElementById("pass").value,
          });
        }}
      >
        Login
      </button>
      <p class="signin">
        have'nt an acount ?
        <a
          onClick={() => {
            navigate("/signup");
          }}
        >
          SIGN UP
        </a>
      </p>
    </form>
  );
};

export default Login;
