import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const AllProject = () => {
  const navigate = useNavigate();
  const [data, setdata] = useState([]);
  
  useEffect(() => {
    axios
      .get("http://localhost:4000/api/projects/")
      .then((result) => {
        setdata(result.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  const getOwner = (e) => {
    return axios.get(`http://localhost:4000/api/users/${e.owner}`)
      .then((result) => {
        return result.data.userName;
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div className="card-container">
      {data &&
        data.map((e, i) => {
          getOwner(e).then((owner) => {
            return (
              <div
                className="card-client"
                onClick={() => {
                  navigate("/allproject/oneproject", {
                    state: { element: e, owner: owner },
                  });
                }}
              >
                <div className="user-picture">
                  <img
                    src={e.screenshotLink}
                    alt={e.name}
                    onClick={() => {
                      window.location.href = e.viewlink;
                    }}
                  />
                </div>
                <p className="name-client"> {e.name}</p>
                <p className="name-client"> {owner}</p>
                <a href={e.viewlink}> Open the website</a>
              </div>
            );
          });
        })}
    </div>
  );
};

export default AllProject;
