import { React, useState, useEffect } from "react";
import useNavigate, { Route, Routes } from "react-router-dom"
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import SideBar from "./components/sidebar/SideBar";
import Content from "./components/content/Content";
import axios from "axios";
import "./App.css";
import Allprojects from "./components/AllProjects.jsx";
import Addproject from "./components/Add.jsx";
import Updateproject from "./components/UpdateProject.js";
import SignUp from "./components/SingUp";
import Login from "./components/Login";
import OneProject from "./components/OneProject.jsx";
import Myprojects from "./components/Myprojects";
import Profile from "./components/Profil";
import UpdateProfile from "./components/UpdateProfile";
function App() {
  const navigate=useNavigate()



  const [view, setView] = useState();
  let changeView = (view) => {
    setView(view);
    settrigger(!trigger);
  };
  const [data, setdata] = useState([]);
  const [trigger, settrigger] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/projects/")
      .then((result) => {
        console.log(result.data);
        setdata(result.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, [trigger]);
  const addproject = (obj) => {
    axios
      .post(`http://localhost:5000/api/projects/addproject/${iduser}`, obj)
      .then((result) => {
        settrigger(!trigger);
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  const deleteproject = (id) => {
    axios
      .delete(`http://localhost:5000/api/projects/deleteproject/${id}`)
      .then((result) => {
        settrigger(!trigger);
        console.log("deleted");
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  const updateproject = (id, obj) => {
    axios
      .put(`http://localhost:5000/api/projects/updateproject/${id}`, obj)
      .then((result) => {
        settrigger(!trigger);
        console.log("updated");
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  const searchproject = (term) => {
    if (!term) {
      settrigger(!trigger);
    }
    setdata(
      data.filter((e, i) => {
        return e.name.toUpperCase().includes(term.toUpperCase());
      })
    );
  };
  const [oneproject, setOneproject] = useState({});
  const one = (e) => {
    setOneproject(e);
  };
  //get user data:
  const [userdata, setuserdata] = useState([]);
  axios
    .get(`http://localhost:5000/api/projects/${userName}`)
    .then((result) => {
      setuserdata(result.data.userName);
    })
    .catch((error) => {
      console.log(error);
    });

  //login:
  const [userName, setuserName] = useState("");
  const [iduser, setiduser] = useState("");
  const confirmuser = (email, password) => {
    axios
      .get(
        `http://localhost:5000/api/users/getbyemailandpassword/${email}/${password}`
      )
      .then((result) => {
        return result.data;
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const verifuser = (obj) => {
    if (emailsaved(obj.email).length === 0) {
      setmessage("this email is not registred anymore");
    } else if (confirmuser(obj.email, obj.password).length === 0) {
      setmessage("password invalid ");
    } else {
      setuserName(emailsaved(obj.email)[0].userName);
      return true;
    }
  };

  //signUp:
  const [message, setmessage] = useState("");

  const emailsaved = (email) => {
    axios
      .get(`http://localhost:5000/api/users/getbyemail/${email}`)
      .then((result) => {
        return result.data;
      })
      .catch((error) => {
        return false;
      });
  };
  const validateEmail = (email) => {
    const re = /\S+@\S+\.\S+/;
    return re.test(email);
  };
  const verif = (obj) => {
    if (obj.name.length === 0 || obj.name.includes("123456789")) {
      return false;
    } else if (obj.email.length === 0 || !validateEmail(obj.email)) {
      setmessage("Invalid Email. Please enter a valid email address.");
      return false;
    } else if (obj.password.length < 8) {
      setmessage(
        "Invalid Password. Please enter a password with a minimum of 8 characters."
      );
    } else if (obj.password !== obj.confirmpass) {
      setmessage("confirm your password correctly");
    } else if (emailsaved(obj.email).length === 0) {
      setmessage("email exist ! try another one");
      return false;
    } else {
      axios
        .post(`http://localhost:5000/api/users/adduser`, {
          userName: obj.userName,
          email: obj.email,
          password: obj.password,
          imageUrl: obj.imageUrl,
          githubLink: obj.githubLink,
        })
        .then((result) => {
          console.log("hello " + obj.name);
        })
        .catch((error) => {
          console.log(error);
        });
      return true;
    }
  };

  return (
    <div className="App wrapper">
      <SideBar changeView={changeView}/>
      <Content changeView={changeView} setSearchTerm={setSearchTerm}/>
      <Routes>
        <Route path='/login' elemene></Route>
      </Routes>

      {view === "Allprojects" && (
        <Allprojects one={one} data={data} changeView={changeView} />
      )}
      {view === "oneproject" && (
        <OneProject
          element={oneproject}
          changeView={changeView}
          deleteproject={deleteproject}
        />
      )}
      {view === "Addproject" && (
        <Myprojects data={data} one={one} changeView={changeView} addproject={addproject} />
      )}
      {view === "Addproject" && (
        <Addproject
          userName={userName}
          changeView={changeView}
          addproject={addproject}
        />
      )}
      {view === "updateproject" && (
        <Updateproject updateproject={updateproject} changeView={changeView} />
      )}
      {view === "login" && (
        <Login verifuser={verifuser} changeView={changeView} />
      )}
      {view === "profil" && (
        <Profile userdata={userdata} changeView={changeView} />
      )}
      {view === "uppprofil" && (
        <UpdateProfile
          verifandupdate={verifandupdate}
          changeView={changeView}
        />
      )}
      {view === "signup" && <SignUp verif={verif} changeView={changeView} />}
    </div>
  );
}

export default App;
