import React from "react";
import "../App.css";

const Myprojects = (props) => {
  return (
    <div className="card-container">
      {props.data &&
        props.data.map((e, i) => {
          return (
            <div
              class="card-client"
              onClick={() => {
                props.one(e);
                props.changeView("oneproject");
              }}
            >
              <div class="user-picture">
                <img src={e.sreenshotLink} alt={e.name} />
              </div>
              <p class="name-client"> {e.name}</p>
              <p>{e.description} </p>
              <a href={e.viewlink}> Open the website</a>
              <br />
              <a href={e.githubLinkRepo}>Open repo on github</a>
            </div>
          );
        })}
      <button
        className="button"
        onClick={() => {
          props.changeView("addproject");
        }}
      >
        Add Project
      </button>
    </div>
  );
};

export default Myprojects;
