import React, { useState } from "react";
import "../App.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Login = (props) => {
  const navigate = useNavigate();
  const [message, setMessage] = useState("");

  const emailsaved = async (email) => {
    try {
      const result = await axios.get(`http://localhost:4000/api/users/getbyemail/${email}`);
      return result.data.length > 0;
    } catch (error) {
      console.error(error);
      return false;
    }
  };

  const confirmuser = async (email, password) => {
    try {
      const result = await axios.get(
        `http://localhost:4000/api/users/getbyemailandpassword/${email}/${password}`
      );
      return result.data.length > 0;
    } catch (error) {
      console.error(error);
      return false;
    }
  };

  const verifuser = async (obj) => {
    const isEmailSaved = await emailsaved(obj.email);
    const isUserConfirmed = await confirmuser(obj.email, obj.password);

    if (isEmailSaved) {
      setMessage("Email is invalid");
    } else if (isUserConfirmed) {
      setMessage("Password is invalid");
    } else {
      return true;
    }
  };

  return (
    <form className="form">
      <p className="title">Login</p>
      <p className="message">Login now and get full access to our app.</p>

      <label>
        <input
          id="email"
          required
          placeholder=""
          type="email"
          className="input"
        />
        <span>Email</span>
      </label>
      <label>
        <input
          id="pass"
          required
          placeholder=""
          type="password"
          className="input"
        />
        <span>Password</span>
      </label>
      <p>{message}</p>
      <button
        className="submit"
        onClick={async (e) => {
          e.preventDefault();
          const isUserVerified = await verifuser({
            email: document.getElementById("email").value,
            password: document.getElementById("pass").value,
          });
          if (isUserVerified) {
            navigate("/allprojects");
          }
        }}
      >
        Login
      </button>
      <p className="signin">
        Haven't an account?{" "}
        <a
          onClick={() => {
            navigate("/signup");
          }}
        >
          SIGN UP
        </a>
      </p>
    </form>
  );
};

export default Login;
