import React from "react";
import "../App.css";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const navigate = useNavigate();
  const [mes, setuserName] = useState("");
  const emailsaved = (email) => {
    axios
      .get(`http://localhost:4000/api/users/getbyemail/${email}`)
      .then((result) => {
        return true
      })
      .catch((error) => {
        return false;
      });
  };
  const confirmuser = (email, password) => {
    axios
      .get(
        `http://localhost:4000/api/users/getbyemailandpassword/${email}/${password}`
      )
      .then((result) => {
        return true
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const verifuser = (obj) => {
    if (emailsaved(obj.email)) {
      setmessage("this email already registred ");
    } else if (!confirmuser(obj.email, obj.password)) {
      setmessage("password invalid ");
    } else {
      setuserName(emailsaved(obj.email)[0].userName);
      return true;
    }
  };

  return (
    <form class="form">
      <p class="title">Login </p>
      <p class="message">Login now and get full access to our app. </p>

      <label>
        <input
          id="email"
          required=""
          placeholder=""
          type="email"
          class="input"
        />
        <span>Email</span>
      </label>
      <label>
        <input
          id="pass"
          required=""
          placeholder=""
          type="password"
          class="input"
        />
        <span>Password</span>
      </label>
      <p>{message}</p>
      <button
        class="submit"
        onClick={(e) => {
          e.preventDefault();
          if (
            verifuser({
              email: document.getElementById("email").value,
              password: document.getElementById("pass").value,
            })
          ) {
            navigate("/allproject");
          }
        }}
      >
        Login
      </button>
      <p class="signin">
        have'nt an acount ?
        <a
          onClick={() => {
            navigate("/signup");
          }}
        >
          SIGN UP
        </a>
      </p>
    </form>
  );
};

export default Login;
