import React from "react";
import "../App.css";
import useNavigate from "react-router-dom";

const Login = (props) => {
  return (
    <form class="form">
      <p class="title">Login </p>
      <p class="message">Login now and get full access to our app. </p>

      <label>
        <input
          id="email"
          required=""
          placeholder=""
          type="email"
          class="input"
        />
        <span>Email</span>
      </label>
      <label>
        <input
          id="pass"
          required=""
          placeholder=""
          type="password"
          class="input"
        />
        <span>Password</span>
      </label>
      <p>{props.message}</p>
      <button
        class="submit"
        onClick={() => {
          if (
            props.verifuser({
              email: document.getElementById("email").value,
              password: document.getElementById("pass").value,
            })
          ) {
            props.changeView("allproject");
          }
        }}
      >
        Submit
      </button>
      <p class="signin">
        have'nt an acount ?
        <a
          onClick={() => {
            props.changeView("signup");
          }}
        >
          SIGN UP
        </a>
      </p>
    </form>
  );
};

export default Login;
