import React, { useEffect, useState } from "react";
import "../App.css";
import { useNavigate } from "react-router-dom";
import axios from "axios"

const Myprojects = (props) => {
  const navigate = useNavigate();
  const deleteproject = (id) => {
    axios
      .delete(`http://localhost:4000/api/projects/deleteproject/${id}`)
      .then((result) => {
        console.log("deleted");
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  const [data, setData] = useState({
        "idprojects": 6,
        "name": "e-bay",
        "description": "an international e-commerce project",
        "viewlink": "https://www.ebay.com",
        "githubLinkRepo": "#",
        "sreenshotLink": "https://i.ibb.co/Xp5vBHw/image-2023-08-16-173704641.png",
        "owner": 3
    },
    {
        "idprojects": 7,
        "name": "humblebundle",
        "description": "a platform for movies",
        "viewlink": "https://www.humblebundle.com",
        "githubLinkRepo": "#",
        "sreenshotLink": "https://i.ibb.co/60RFyk3/image-2023-08-16-173847123.png",
        "owner": 3
    },
    {
        "idprojects": 8,
        "name": "Eneba",
        "description": "a platform for games",
        "viewlink": "https://www.eneba.com",
        "githubLinkRepo": "#",
        "sreenshotLink": "https://i.ibb.co/yfFr9pZ/image-2023-08-16-174144735.png",
        "owner": 3
    });
  useEffect(() => {
    axios
      .get(`http://localhost:4000/api/projects/byowner/${props.data.idusers}`)
      .then((result) => {
        setData(result.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  return (
    <div className="card-container">
      {data &&
        data.map((e, i) => {
          return (
            <div class="card-client">
              <div class="user-picture">
                <img src={e.sreenshotLink} alt={e.name} />
              </div>
              <p class="name-client"> {e.name}</p>
              <p>{e.description} </p>
              <a href={e.viewlink}> Open the website</a>
              <br />
              <a href={e.githubLinkRepo}>Open repo on github</a>
              <button
                className="button"
                onClick={() => {
                  navigate("/myprojects/updateproject", {
                    state: { id: e.idprojects },
                  });
                }}
              >
                update
              </button>
              <button
                className="button"
                onClick={() => {
                  deleteproject(e.idprojects);
                }}
              >
                delete
              </button>
            </div>
          );
        })}
      <button
        className="button"
        onClick={() => {
          navigate("/myprojects/addproject");
        }}
      >
        Add Project
      </button>
    </div>
  );
};

export default Myprojects;
