import { React, useState, useEffect } from "react";
import {
  useNavigate,
  Route,
  Routes,
  BrowserRouter,
  useLocation,
} from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import axios, { Axios } from "axios";

import SideBar from "./components/sidebar/SideBar";
import NavBar from "./components/content/Navbar";
import Allprojects from "./components/AllProjects.jsx";
import Addproject from "./components/Add.jsx";
import Updateproject from "./components/UpdateProject.js";
import SignUp from "./components/SingUp";
import Login from "./components/Login";
import OneProject from "./components/OneProject.jsx";
import Myprojects from "./components/Myprojects";
import Profile from "./components/Profil";
import UpdateProfile from "./components/UpdateProfile";

function App() {
  const navigate = useNavigate();
  const location = useLocation();

  //get user data:
  const [userdata, setuserdata] = useState([]);
  axios
    .get(`http://localhost:4000/api/users/${location.state.id}`)
    .then((result) => {
      setuserdata(result.data);
    })
    .catch((error) => {
      console.log(error);
    });

  return (
    <BrowserRouter className="App wrapper">
      <Routes>
        <Route path="/" elemenet={<SideBar />} />
        <Route path="/" elemenet={<NavBar />} />
        <Route path="/" elemenet={<Login />} />
        <Route path="/singup" elemenet={<SignUp />} />
        <Route path="/allproject" elemenet={<Allprojects />} />
        <Route path="allproject/oneproject" elemenet={<OneProject />} />
        <Route path="/myprojects" elemenet={<Myprojects />} />
        <Route path="/myprojects/addproject" elemenet={<Addproject />} />
        <Route path="/myprojects/updateproject" elemenet={<Updateproject />} />
        <Route path="/profile" elemenet={<Profile />} />
        <Route path="/profile/updateprofile" elemenet={<UpdateProfile />} />
      </Routes>
      <hr />
      <button
        onClick={() => {
          navigate(-1);
        }}
      >
        Go back
      </button>
      <button
        onClick={() => {
          navigate(1);
        }}
      >
        Go forward
      </button>
    </BrowserRouter>
  );
}

export default App;
