import React, { useState,useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios"

const AllRecepies = () => {
  const navigate = useNavigate();
  const [data, setdata] = useState([]);
  useEffect(() => {
    axios
      .get("http://localhost:4000/api/projects/")
      .then((result) => {
        setdata(result.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);
  const theOwner = (e) => {
    axios
      .get(`http://localhost:4000/api/users/${e.owner}`)
      .then((result) => {
        return result.data.userName;
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div className="card-container">
      {data &&
        data.map((e, i) => {
          var owner=theOwner(e)
          return (
            <div
              class="card-client"
              onClick={() => {
                navigate("/allproject/oneproject", { state: { element: e ,owner:owner} });
              }}
            >
              <div class="user-picture">
                <img
                  src={e.sreenshotLink}
                  alt={e.name}
                  onClick={() => {
                    return <a href={e.viewlink}></a>;
                  }}
                />
              </div>
              <p class="name-client"> {e.name}</p>
              <p class="name-client"> {owner}</p>
              <a href={e.viewlink}> Open the website</a>
            </div>
          );
        })}
    </div>
  );
};

export default AllRecepies;
